import { useState, useEffect } from 'react';

export default function Title() {
    const useTitle = () => {
      const [title, setTitle] = useState(null);

      const updateTitle = () => {
        const htmlTitle = document.querySelector("title");
        htmlTitle.innerHTML = title;
      };
      useEffect(updateTitle, [title]);

      return setTitle;
    };

    return useTitle;
}
