import React from 'react';
import gfm from 'remark-gfm'
import ReactMarkdown from 'react-markdown'
import {Prism as SyntaxHighlighter} from 'react-syntax-highlighter'
import {okaidia} from 'react-syntax-highlighter/dist/esm/styles/prism'

import DetailModel from "../model/DetailModel";

function InlineCodeBlock(props) {
  return (
    <span style={{background: '#ff0'}}>
      {props.value}
    </span>
  );
}

function BlockQuoteBlock(props) {
  return (
    <span style={{border: '1px dashed #aaa', borderRadius: 10, paddingLeft: 10, margin: 5}}>
      {props.children}
    </span>
  );
}

function TableCellBlock(props) {
  let style = {
    textAlign: props.align ? props.align : 'center',
    padding: 5
  };

  if (props.isHeader) {
    style.background = '#ff0';
    style.border = '1px solid #ccc';
    style.boderLeft = 0;
    style.borderRight = 0;
  } else {
    style.borderBottom = '1px solid #eee';
  }

  return (
    <td style={style}>
      {props.children}
    </td>
  );
}

export default function Detail(props) {
    // 상세 내용 취득
    const getDetailData = DetailModel(props.match.params.id);
    const detailData = getDetailData[0];
    console.log(detailData);
    var markdown = "";

    const renderers = {
        code: ({language, value}) => {
            return <SyntaxHighlighter style={okaidia} language={language} children={value} />
        },
        tableCell: TableCellBlock,
        inlineCode: InlineCodeBlock,
        blockquote: BlockQuoteBlock,
    }

    if (detailData) {
        markdown = detailData.board_article;
    }

    return (
        <>
        <ReactMarkdown plugins={[gfm]} renderers={renderers} children={markdown} />
        </>
    );
}
