import { useState, useEffect } from 'react';
import axios from 'axios'

export default function MainListModel() {
    // 추가 데이터를 로드하는지 아닌지를 담기위한 state
    const [fetching, setFetching] = useState(false);
    // 리스트 데이터 저장
    const [jsonData, jsonDataSet] = useState([]);
    // 다음 페이지 데이터를 저장
    // const [getPaging, setPaging] = useState<IPagingData>({ next: undefined });

    // 메인 리스트 취득
    const mainFetchData = async () => {
        try {
            const response = await axios.get('https://cocoa.akibatv.net/?/api/blog_playneko_main?page=0');
            jsonDataSet(response.data.list);
        } catch(e) {
            console.log(e);
        }
    }

    // 추가 리스트 취득
    const moreFetchData = async () => {
        // 추가 데이터를 로드하는 상태로 전환
        setFetching(true);

        try {
            const response = await axios.get('https://cocoa.akibatv.net/?/api/blog_playneko_main');
            // 추가 데이터 부분
            const fetchedData = response.data.list;
            // 기존 데이터 배열과 새로 받아온 데이터 배열을 합쳐 새 배열을 만들고 state에 저장한다. 
            const mergedData = jsonData.concat(...fetchedData);
            console.log(mergedData);
            jsonDataSet(mergedData);
        } catch(e) {
            console.log(e);
        }

        // 추가 데이터 로드 끝
        setFetching(false);
    }

    // 스크롤 이벤트 핸들러
    const handleScroll = () => {
        const scrollHeight = document.documentElement.scrollHeight;
        const scrollTop = document.documentElement.scrollTop;
        const clientHeight = document.documentElement.clientHeight;
        if (scrollTop + clientHeight >= scrollHeight && fetching === false) {
            // 페이지 끝에 도달하면 추가 데이터를 받아온다
            moreFetchData();
        }
    };

    useEffect(() => {
        // 메인 리스트 가져오기
        mainFetchData();
        console.log(jsonData);

        // scroll event listener 등록
        window.addEventListener("scroll", handleScroll);
        return () => {
            // scroll event listener 해제
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return jsonData;
}
