// 리스트 데이터 저장
const [jsonData, jsonDataSet] = useState([]);

// 메인 리스트 취득
const mainFetchData = async () => {
    try {
        const response = await axios.get('https://cocoa.akibatv.net/?/api/blog_playneko_main?page=0');
        if (response.data != null) {
            jsonDataSet(response.data.list);
        }
    } catch(e) {
        console.log(e);
    }
}

useEffect(() => {
    // 메인 리스트 가져오기
    mainFetchData();
}, []);

export default jsonData;
